from shiny import App, render, ui
import pandas as pd

app_ui = ui.page_navbar(
       ui.nav("Home", 
            ui.input_slider("n", "N", 0, 100, 40),
            ui.output_text_verbatim("txt"),
            ),
       ui.nav("Algorithme", 
              ui.input_numeric("nbtimes", "Times", 5),
              ui.output_text("looptimes"),
              ),
        ui.nav("Load csv data", 
               ui.input_file("csvfile", "Fichier csv", accept=".csv"),
               ui.input_text("sep", "SÃ©parateur", ","),
               ui.output_text("csvtext", "Fichier csv en format texte"),
               ui.output_data_frame("showdf")
               ),
       title = "Data science"
)




def server(input, output, session):
    @output
    @render.text
    def txt():
        return f"n*2 is {input.n() * 2}"

    @output
    @render.text
    def looptimes():
        return [x for x in range(input.nbtimes())]

    @output
    @render.text
    def csvtext():
        if input.csvfile() is None:
            return "Aucun fichier sÃ©lectionnÃ©."
        return input.csvfile()[0]["datapath"]

    @output
    @render.data_frame
    def showdf():
        if input.csvfile() is None:
            return
        return render.DataGrid(pd.read_csv(input.csvfile()[0]["datapath"], sep=input.sep()))



app = App(app_ui, server)
